##### POUR LIKE  25 TWEET AVEC UN MOT PRECIS client.favorite ######


def likeTweet(client)

    client.search("#bonjour_monde", result_type: "recent").take(25).collect do |tweet|
     puts "#{tweet.user.screen_name}: \n #{tweet.text} : \n #{tweet.user.location}: \n #{tweet.user.screen_name} :   #{tweet.user.id} "
          client.favorite(tweet.id)
    end
            puts "ADD LIKE UP"
end
   

##### POUR FOLLOW  25 TWEETER AVEC UN MOT PRECIS client.follow ######

def followTweet(client)

    client.search("#bonjour_monde", result_type: "recent").take(25).collect do |tweet|
     puts "#{tweet.user.screen_name}: \n #{tweet.text} : \n #{tweet.user.location}: \n #{tweet.user.screen_name} :   #{tweet.user.id} "
         client.follow(tweet.user.id)
    end
            puts "ADD FOLLOW UP"
end
   

# commande pour afficher mes infos



def liking_and_follow_25_recents_bonjour(client)

  
    
    client.search('#bonjour_monde',reslut_type: 'recent').take(25).collect do |k|
      client.favorite(k.id)
      puts "VOUS AVEZ LIKEZ LE POST SUIVANT : \n¬∞¬∞¬∞\n  #{k.text} \n¬∞¬∞¬∞\n NUMERO : #{k.id}\n¬∞¬∞¬∞\n AUTEUR : #{k.user.name.upcase}."
      client.follow(k.user.id)
      puts "vous suivez maintenant #{k.user.name}."
      sleep (30)
    end
  end


  

